var temps = "14 17.8 -12.8 8.7 10 21 -15.2 16.8 -14.1 11.2 -7.2 -12.8 15.8";

//trouver le plus petit entier qui se rapproche de zero
//le delimiteur est l'espace
tablo = temps.split(' ');
var k;

console.log(tablo);
for(var i=0; i<tablo.length; i++){
    
    if(typeof k == 'undefined'){

        k = tablo[i];
    }
    else 
    {
        //methode Math.abs retourne la valeur absolu de k (enleve le signe) 
        if ( Math.abs(parseInt(k)) > Math.abs(parseInt(tablo[i])) )
        {
            k = tablo[i];
            console.log('boucle : '+tablo);
        }
    }
}
console.log('resultat :'+k)